const anchorTags = document.querySelectorAll('a');
anchorTags.forEach((anchor) => {
    const href = anchor.getAttribute('href');

    if (!href || href === '#' || href.startsWith('#')) {
        anchor.setAttribute('href', 'javascript:;');
    }
});